import { CalendarService } from './../../services/calendar.service';
import { catchError, Observable, of } from 'rxjs';
import { Calendar } from './../../model/calendar';
import { Component, Input } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-event-component',
  templateUrl: './event-component.component.html',
  styleUrls: ['./event-component.component.css']
})
export class EventComponentComponent {

  calendars$: Observable<Calendar[]> | null = null;
  @Input() calendar: Calendar[] = [];

  constructor(
    private calendarService: CalendarService,
    private router: Router) {

    this.refresh();
  }

  refresh(){
    this.calendars$ = this.calendarService.getCalendar().pipe(
      catchError(
        error => {
          return of([]);
        })
    );
  }

  onEdit(calendar: Calendar){
    this.router.navigate(['Editar', calendar.id]);
  }

}
<div *ngIf="calendars$ | async as calendars;">
<div class="__cointainer__event shadow" *ngFor="let calendar of calendars">
  <div class="container">
    <div class="row">
      <div class="row">

        <div class="col-xs-12 col-sm-12 col-md-3 col-lg-3 ">
          <span class="__span__header">{{calendar.titulo}}</span>
        </div>
        <div class="col-xs-12 col-sm-12 col-md-3 col-lg-3 ">
          <span class="__span__header">{{calendar.juiz}}</span>
        </div>
        <div class="col-xs-12 col-sm-12 col-md-3 col-lg-2 ">
          <span class="__span__header">{{calendar.dia}}</span>
        </div>
        <div class="col-xs-12 col-sm-12 col-md-3 col-lg-1 ">
          <span class="__span__header">{{calendar.inicio}}</span>
        </div>
        <div class="col-xs-12 col-sm-12 col-md-3 col-lg-1 ">
          <span class="__span__header">{{calendar.fim}}</span>
        </div>
        <div class="col-xs-12 col-sm-12 col-md-3 col-lg-1 ">
          <span><img src="../../assets/edit.png" alt="" width="50%"></span>
        </div>
        <div class="col-xs-12 col-sm-12 col-md-3 col-lg-1 ">
          <span><img src="../../assets/remove.png" alt="" width="50%"></span>
        </div>

      </div>
    </div>
  </div>
</div>
</div>
